```java
package com.belvinard.gestionstock.service.impl;

import com.belvinard.gestionstock.dto.MvtStkDTO;
import com.belvinard.gestionstock.exception.InvalidOperationException;
import com.belvinard.gestionstock.exception.ResourceNotFoundException;
import com.belvinard.gestionstock.models.*;
import com.belvinard.gestionstock.repository.ArticleRepository;
import com.belvinard.gestionstock.repository.MvtStkRepository;
import com.belvinard.gestionstock.service.MvtStkService;
import lombok.RequiredArgsConstructor;
import org.modelmapper.ModelMapper;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import java.math.BigDecimal;
import java.time.LocalDateTime;
import java.util.List;
import java.util.stream.Collectors;

@Service
@RequiredArgsConstructor
public class MvtStkServiceImpl implements MvtStkService {

 private final MvtStkRepository mvtStkRepository;
 private final ArticleRepository articleRepository;
 private final ModelMapper modelMapper;

 @Override
 @Transactional
 public MvtStkDTO save(MvtStkDTO dto) {
 // Validate article
 Article article = articleRepository.findById(dto.getIdArticle())
 .orElseThrow(() -> new ResourceNotFoundException("Article", "id", dto.getIdArticle()));

 // Validate stock for SORTIE operations
 if (dto.getTypeMvt() == TypeMvtStk.SORTIE) {
 if (article.getQuantiteEnStock() < dto.getQuantite().longValue()) {
 throw new InvalidOperationException("Stock insuffisant. Stock actuel: " + 
 article.getQuantiteEnStock() + ", Quantité demandée: " + dto.getQuantite());
 }
 }

 // Update article stock
 updateArticleStock(article, dto.getQuantite(), dto.getTypeMvt());

 // Create movement record
 MvtStk mvtStk = modelMapper.map(dto, MvtStk.class);
 mvtStk.setArticle(article);
 mvtStk.setDateMvt(LocalDateTime.now());

 MvtStk saved = mvtStkRepository.save(mvtStk);
 return modelMapper.map(saved, MvtStkDTO.class);
 }

 @Override
 @Transactional
 public MvtStkDTO entreeStock(Long articleId, BigDecimal quantite, String sourceMvt) {
 MvtStkDTO dto = MvtStkDTO.builder()
 .idArticle(articleId)
 .quantite(quantite)
 .typeMvt(TypeMvtStk.ENTREE)
 .sourceMvt(SourceMvtStk.valueOf(sourceMvt))
 .build();
 return save(dto);
 }

 @Override
 @Transactional
 public MvtStkDTO sortieStock(Long articleId, BigDecimal quantite, String sourceMvt) {
 MvtStkDTO dto = MvtStkDTO.builder()
 .idArticle(articleId)
 .quantite(quantite)
 .typeMvt(TypeMvtStk.SORTIE)
 .sourceMvt(SourceMvtStk.valueOf(sourceMvt))
 .build();
 return save(dto);
 }

 @Override
 @Transactional
 public MvtStkDTO correctionStock(Long articleId, BigDecimal quantite, String sourceMvt) {
 MvtStkDTO dto = MvtStkDTO.builder()
 .idArticle(articleId)
 .quantite(quantite)
 .typeMvt(TypeMvtStk.CORRECTION_POS)
 .sourceMvt(SourceMvtStk.valueOf(sourceMvt))
 .build();
 return save(dto);
 }

 @Override
 public BigDecimal stockReelArticle(Long articleId) {
 Article article = articleRepository.findById(articleId)
 .orElseThrow(() -> new ResourceNotFoundException("Article", "id", articleId));
 return BigDecimal.valueOf(article.getQuantiteEnStock());
 }

 @Override
 public List<MvtStkDTO> mvtStkArticle(Long articleId) {
 return mvtStkRepository.findAllByArticleIdOrderByDateMvtDesc(articleId).stream()
 .map(mvt -> modelMapper.map(mvt, MvtStkDTO.class))
 .collect(Collectors.toList());
 }

 @Override
 public MvtStkDTO findById(Long id) {
 MvtStk mvtStk = mvtStkRepository.findById(id)
 .orElseThrow(() -> new ResourceNotFoundException("MvtStk", "id", id));
 return modelMapper.map(mvtStk, MvtStkDTO.class);
 }

 @Override
 public List<MvtStkDTO> findAll() {
 return mvtStkRepository.findAll().stream()
 .map(mvt -> modelMapper.map(mvt, MvtStkDTO.class))
 .collect(Collectors.toList());
 }

 @Override
 @Transactional
 public void delete(Long id) {
 MvtStk mvtStk = mvtStkRepository.findById(id)
 .orElseThrow(() -> new ResourceNotFoundException("MvtStk", "id", id));

 // Reverse the stock movement before deletion
 reverseStockMovement(mvtStk);
 mvtStkRepository.deleteById(id);
 }

 @Override
 public List<MvtStkDTO> findByDateRange(LocalDateTime startDate, LocalDateTime endDate) {
 return mvtStkRepository.findByDateMvtBetween(startDate, endDate).stream()
 .map(mvt -> modelMapper.map(mvt, MvtStkDTO.class))
 .collect(Collectors.toList());
 }

 @Override
 public List<MvtStkDTO> findByTypeMvt(TypeMvtStk typeMvt) {
 return mvtStkRepository.findByTypeMvt(typeMvt).stream()
 .map(mvt -> modelMapper.map(mvt, MvtStkDTO.class))
 .collect(Collectors.toList());
 }

 @Override
 public List<MvtStkDTO> findBySourceMvt(SourceMvtStk sourceMvt) {
 return mvtStkRepository.findBySourceMvt(sourceMvt).stream()
 .map(mvt -> modelMapper.map(mvt, MvtStkDTO.class))
 .collect(Collectors.toList());
 }

 private void updateArticleStock(Article article, BigDecimal quantite, TypeMvtStk typeMvt) {
 Long currentStock = article.getQuantiteEnStock();
 Long newStock;

 switch (typeMvt) {
 case ENTREE:
 case CORRECTION_POS:
 newStock = currentStock + quantite.longValue();
 break;
 case SORTIE:
 case CORRECTION_NEG:
 newStock = currentStock - quantite.longValue();
 break;
 default:
 throw new InvalidOperationException("Type de mouvement non supporté: " + typeMvt);
 }

 article.setQuantiteEnStock(newStock);
 articleRepository.save(article);
 }

 private void reverseStockMovement(MvtStk mvtStk) {
 Article article = mvtStk.getArticle();
 TypeMvtStk reverseType;

 switch (mvtStk.getTypeMvt()) {
 case ENTREE:
 case CORRECTION_POS:
 reverseType = TypeMvtStk.SORTIE;
 break;
 case SORTIE:
 case CORRECTION_NEG:
 reverseType = TypeMvtStk.ENTREE;
 break;
 default:
 throw new InvalidOperationException("Cannot reverse movement type: " + mvtStk.getTypeMvt());
 }

 updateArticleStock(article, mvtStk.getQuantite(), reverseType);
 }
}
```
</augment_code_snippet>

This implementation provides:

1. **Automatic Stock Updates**: Updates `quantiteEnStock` based on movement type
2. **Transaction Safety**: All methods are `@Transactional`
3. **Validation**: Checks stock before SORTIE operations
4. **Audit Trail**: Creates MvtStk records for all movements
5. **Stock Reversal**: Properly reverses movements when deleting records
6. **Comprehensive Methods**: All interface methods implemented with proper error handling

The service integrates with your existing entities and provides a complete stock movement tracking system.
